graphql.query:
query ($id: ID! = "00000000-0000-0000-0000-000000000001", $first: Int! = 2) {
  mannschaft(id: $id) {
    version
    nachname
    email
    kategorie
    hasNewsletter
    geburtsdatum
    homepage
    geschlecht
    familienstand
    interessen
    adresse {
      plz
      ort
    }
    umsaetze(first: $first) {
        betrag
    }
  }
}

